CREATE TABLE `proyectoapple`.`bloques` (`Identificador` INT(255) NOT NULL AUTO_INCREMENT , `categorias_nombre` INT(255) NOT NULL , `tipobloque_tipo` INT(255) NOT NULL , `titulo` VARCHAR(255) NOT NULL , `subtitulo` VARCHAR(255) NOT NULL , `texti` TEXT NOT NULL , `imagen` MEDIUMBLOB NOT NULL , `fondo` MEDIUMBLOB NOT NULL , `estilo` VARCHAR(255) NOT NULL , PRIMARY KEY (`Identificador`)) ENGINE = InnoDB;
